mapper.select:
SELECT *
FROM alfuserlibrary ,
(select userId AS id,COUNT(*),MIN(`timestamp`),MAX(`timestamp`) FROM record

  where
    timestamp between #{beginTime} and #{endTime}

  GROUP BY userId)
AS re

  re.id = alfuserlibrary.userId


    and type = #{type}


    and groupName = #{group}


    and userName = #{name}

select
record._id, groupId, userName, signAvatar, signBgAvatar,
signTime, record.type, timestamp,userId,_group.groupName as groupName
from record,_group

record.groupId = _group._id

   and timestamp between #{beginTime} and #{endTime}


    and record.type = #{type}


    and _group.groupName = #{group}


    and record.userName = #{name}



order by timestamp desc

select count(*) from record 

    timestamp between #{beginTime} and #{endTime}

        and type = #{type}
